import React from 'react';
import {
  Drawer,
  Box,
  Typography,
  IconButton,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Button,
  Stack,
  Card,
  CardContent,
  Badge,
} from '@mui/material';
import {
  Close as CloseIcon,
  Add as AddIcon,
  Remove as RemoveIcon,
  Delete as DeleteIcon,
  ShoppingBag as ShoppingBagIcon,
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import {
  selectCartItems,
  selectCartIsOpen,
  selectCartGrandTotal,
  selectCartRestaurant,
  closeCart,
  updateQuantity,
  removeFromCart,
} from '../../store/slices/cartSlice';

const CartDrawer = styled(Drawer)(({ theme }) => ({
  '& .MuiDrawer-paper': {
    width: 400,
    maxWidth: '90vw',
    padding: theme.spacing(2),
  },
}));

const CartHeader = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  marginBottom: theme.spacing(2),
}));

const CartItem = styled(Card)(({ theme }) => ({
  marginBottom: theme.spacing(2),
  boxShadow: 'none',
  border: `1px solid ${theme.palette.divider}`,
}));

const QuantityControls = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  gap: theme.spacing(1),
  marginTop: theme.spacing(1),
}));

const CheckoutButton = styled(Button)(({ theme }) => ({
  width: '100%',
  marginTop: theme.spacing(2),
  padding: theme.spacing(1.5),
  fontSize: '1rem',
  fontWeight: 600,
}));

const CartSidebar = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  
  const cartItems = useSelector(selectCartItems);
  const isOpen = useSelector(selectCartIsOpen);
  const grandTotal = useSelector(selectCartGrandTotal);
  const restaurant = useSelector(selectCartRestaurant);

  const handleClose = () => {
    dispatch(closeCart());
  };

  const handleQuantityChange = (menuItemId, newQuantity, customizations) => {
    dispatch(updateQuantity({ menuItemId, quantity: newQuantity, customizations }));
  };

  const handleRemoveItem = (menuItemId, customizations) => {
    dispatch(removeFromCart({ menuItemId, customizations }));
  };

  const handleCheckout = () => {
    dispatch(closeCart());
    navigate('/checkout');
  };

  const formatPrice = (price) => {
    return `$${price.toFixed(2)}`;
  };

  if (cartItems.length === 0) {
    return (
      <CartDrawer anchor="right" open={isOpen} onClose={handleClose}>
        <CartHeader>
          <Typography variant="h6">Your Cart</Typography>
          <IconButton onClick={handleClose}>
            <CloseIcon />
          </IconButton>
        </CartHeader>
        
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            height: '60%',
            textAlign: 'center',
          }}
        >
          <ShoppingBagIcon sx={{ fontSize: 64, color: 'grey.400', mb: 2 }} />
          <Typography variant="h6" sx={{ mb: 1, color: 'grey.600' }}>
            Your cart is empty
          </Typography>
          <Typography variant="body2" sx={{ color: 'grey.500', mb: 3 }}>
            Add items from a restaurant to start your order
          </Typography>
          <Button variant="contained" onClick={handleClose}>
            Browse Restaurants
          </Button>
        </Box>
      </CartDrawer>
    );
  }

  return (
    <CartDrawer anchor="right" open={isOpen} onClose={handleClose}>
      <CartHeader>
        <Box>
          <Typography variant="h6">Your Cart</Typography>
          {restaurant.name && (
            <Typography variant="body2" sx={{ color: 'grey.600' }}>
              from {restaurant.name}
            </Typography>
          )}
        </Box>
        <IconButton onClick={handleClose}>
          <CloseIcon />
        </IconButton>
      </CartHeader>

      <Divider />

      {/* Cart Items */}
      <List sx={{ flexGrow: 1, overflow: 'auto', py: 2 }}>
        {cartItems.map((item, index) => (
          <ListItem key={`${item.menuItem.id}-${index}`} sx={{ px: 0 }}>
            <CartItem sx={{ width: '100%' }}>
              <CardContent sx={{ p: 2, '&:last-child': { pb: 2 } }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                  <Box sx={{ flexGrow: 1 }}>
                    <Typography variant="subtitle1" sx={{ fontWeight: 600, mb: 0.5 }}>
                      {item.menuItem.name}
                    </Typography>
                    
                    {item.customizations && item.customizations.length > 0 && (
                      <Typography variant="body2" sx={{ color: 'grey.600', mb: 1 }}>
                        {item.customizations.join(', ')}
                      </Typography>
                    )}
                    
                    <Typography variant="body2" sx={{ color: 'primary.main', fontWeight: 600 }}>
                      {formatPrice(item.menuItem.price)}
                    </Typography>
                  </Box>
                  
                  <IconButton
                    size="small"
                    onClick={() => handleRemoveItem(item.menuItem.id, item.customizations)}
                    sx={{ color: 'error.main' }}
                  >
                    <DeleteIcon fontSize="small" />
                  </IconButton>
                </Box>

                <QuantityControls>
                  <IconButton
                    size="small"
                    onClick={() => handleQuantityChange(item.menuItem.id, item.quantity - 1, item.customizations)}
                    disabled={item.quantity <= 1}
                  >
                    <RemoveIcon />
                  </IconButton>
                  
                  <Badge badgeContent={item.quantity} color="primary">
                    <Box sx={{ width: 32, height: 32 }} />
                  </Badge>
                  
                  <IconButton
                    size="small"
                    onClick={() => handleQuantityChange(item.menuItem.id, item.quantity + 1, item.customizations)}
                  >
                    <AddIcon />
                  </IconButton>
                  
                  <Typography variant="body2" sx={{ ml: 'auto', fontWeight: 600 }}>
                    {formatPrice(item.itemTotal)}
                  </Typography>
                </QuantityControls>
              </CardContent>
            </CartItem>
          </ListItem>
        ))}
      </List>

      <Divider />

      {/* Cart Summary */}
      <Box sx={{ mt: 2 }}>
        <Stack spacing={1} sx={{ mb: 2 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
            <Typography variant="body2">Subtotal</Typography>
            <Typography variant="body2">{formatPrice(grandTotal - 2.99 - (grandTotal * 0.08))}</Typography>
          </Box>
          <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
            <Typography variant="body2">Delivery Fee</Typography>
            <Typography variant="body2">{formatPrice(2.99)}</Typography>
          </Box>
          <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
            <Typography variant="body2">Taxes</Typography>
            <Typography variant="body2">{formatPrice(grandTotal * 0.08)}</Typography>
          </Box>
          <Divider />
          <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
            <Typography variant="h6" sx={{ fontWeight: 600 }}>Total</Typography>
            <Typography variant="h6" sx={{ fontWeight: 600, color: 'primary.main' }}>
              {formatPrice(grandTotal)}
            </Typography>
          </Box>
        </Stack>

        <CheckoutButton
          variant="contained"
          onClick={handleCheckout}
          disabled={cartItems.length === 0}
        >
          Proceed to Checkout
        </CheckoutButton>
      </Box>
    </CartDrawer>
  );
};

export default CartSidebar;
