import React from 'react';
import {
  Drawer,
  Box,
  Typography,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Button,
  Divider,
  Chip,
  Card,
  CardContent,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import {
  Close as CloseIcon,
  Add as AddIcon,
  Remove as RemoveIcon,
  Delete as DeleteIcon,
  ShoppingCart as CartIcon,
  Storefront as RestaurantIcon,
} from '@mui/icons-material';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { 
  updateCartItemQuantity, 
  removeFromCart, 
  clearCart,
  toggleCartSidebar 
} from '../../store/slices/cartSlice';
import { useAuth } from '../auth/AuthProvider';

const CartSidebar = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { openLoginModal } = useAuth();
  
  const { 
    items, 
    isOpen, 
    restaurantId, 
    restaurantName,
    subtotal,
    deliveryFee,
    taxes,
    total,
    itemCount 
  } = useSelector((state) => state.cart);
  
  const { isAuthenticated } = useSelector((state) => state.auth);

  const handleClose = () => {
    dispatch(toggleCartSidebar());
  };

  const handleQuantityChange = (menuItemId, newQuantity) => {
    if (newQuantity <= 0) {
      dispatch(removeFromCart(menuItemId));
    } else {
      dispatch(updateCartItemQuantity({ menuItemId, quantity: newQuantity }));
    }
  };

  const handleRemoveItem = (menuItemId) => {
    dispatch(removeFromCart(menuItemId));
  };

  const handleClearCart = () => {
    dispatch(clearCart());
  };

  const handleCheckout = () => {
    if (!isAuthenticated) {
      openLoginModal();
      return;
    }
    
    handleClose();
    navigate('/checkout');
  };

  const handleViewRestaurant = () => {
    if (restaurantId) {
      handleClose();
      navigate(`/restaurant/${restaurantId}`);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
    }).format(price);
  };

  return (
    <Drawer
      anchor="right"
      open={isOpen}
      onClose={handleClose}
      variant={isMobile ? 'temporary' : 'persistent'}
      sx={{
        width: { xs: '100%', sm: 400 },
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: { xs: '100%', sm: 400 },
          boxSizing: 'border-box',
        },
      }}
    >
      <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        {/* Header */}
        <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <CartIcon color="primary" />
              <Typography variant="h6" sx={{ fontWeight: 600 }}>
                Cart ({itemCount})
              </Typography>
            </Box>
            <IconButton onClick={handleClose} size="small">
              <CloseIcon />
            </IconButton>
          </Box>
          
          {restaurantName && (
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mt: 1 }}>
              <RestaurantIcon sx={{ fontSize: 16, color: 'text.secondary' }} />
              <Typography 
                variant="body2" 
                color="text.secondary"
                sx={{ cursor: 'pointer', textDecoration: 'underline' }}
                onClick={handleViewRestaurant}
              >
                {restaurantName}
              </Typography>
            </Box>
          )}
        </Box>

        {/* Cart Items */}
        {items.length === 0 ? (
          <Box sx={{ 
            flex: 1, 
            display: 'flex', 
            flexDirection: 'column', 
            justifyContent: 'center', 
            alignItems: 'center',
            p: 3,
            textAlign: 'center'
          }}>
            <CartIcon sx={{ fontSize: 64, color: 'text.secondary', mb: 2 }} />
            <Typography variant="h6" color="text.secondary" gutterBottom>
              Your cart is empty
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
              Add some delicious items from our restaurants
            </Typography>
            <Button 
              variant="contained" 
              onClick={() => {
                handleClose();
                navigate('/');
              }}
            >
              Browse Restaurants
            </Button>
          </Box>
        ) : (
          <>
            {/* Cart Items List */}
            <Box sx={{ flex: 1, overflow: 'auto' }}>
              <List>
                {items.map((item) => (
                  <ListItem key={item.menuItemId} sx={{ py: 2 }}>
                    <Box sx={{ display: 'flex', width: '100%', gap: 2 }}>
                      {/* Item Image */}
                      {item.imageUrl && (
                        <Box
                          component="img"
                          src={item.imageUrl}
                          alt={item.name}
                          sx={{
                            width: 60,
                            height: 60,
                            objectFit: 'cover',
                            borderRadius: 1,
                          }}
                        />
                      )}
                      
                      {/* Item Details */}
                      <Box sx={{ flex: 1 }}>
                        <Typography variant="subtitle2" sx={{ fontWeight: 600, mb: 0.5 }}>
                          {item.name}
                        </Typography>
                        <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                          {formatPrice(item.price)} each
                        </Typography>
                        
                        {/* Customizations */}
                        {item.customizations && item.customizations.length > 0 && (
                          <Box sx={{ mb: 1 }}>
                            {item.customizations.map((customization, index) => (
                              <Chip
                                key={index}
                                label={customization}
                                size="small"
                                variant="outlined"
                                sx={{ mr: 0.5, mb: 0.5 }}
                              />
                            ))}
                          </Box>
                        )}
                        
                        {/* Quantity Controls */}
                        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <IconButton
                              size="small"
                              onClick={() => handleQuantityChange(item.menuItemId, item.quantity - 1)}
                              sx={{ bgcolor: 'grey.100' }}
                            >
                              <RemoveIcon fontSize="small" />
                            </IconButton>
                            <Typography sx={{ minWidth: 24, textAlign: 'center', fontWeight: 600 }}>
                              {item.quantity}
                            </Typography>
                            <IconButton
                              size="small"
                              onClick={() => handleQuantityChange(item.menuItemId, item.quantity + 1)}
                              sx={{ bgcolor: 'primary.main', color: 'white' }}
                            >
                              <AddIcon fontSize="small" />
                            </IconButton>
                          </Box>
                          
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <Typography variant="subtitle2" sx={{ fontWeight: 600 }}>
                              {formatPrice(item.price * item.quantity)}
                            </Typography>
                            <IconButton
                              size="small"
                              onClick={() => handleRemoveItem(item.menuItemId)}
                              color="error"
                            >
                              <DeleteIcon fontSize="small" />
                            </IconButton>
                          </Box>
                        </Box>
                      </Box>
                    </Box>
                  </ListItem>
                ))}
              </List>
              
              {/* Clear Cart Button */}
              {items.length > 0 && (
                <Box sx={{ p: 2 }}>
                  <Button
                    variant="outlined"
                    color="error"
                    size="small"
                    onClick={handleClearCart}
                    fullWidth
                  >
                    Clear Cart
                  </Button>
                </Box>
              )}
            </Box>

            {/* Cart Summary */}
            <Box sx={{ borderTop: 1, borderColor: 'divider', p: 2 }}>
              <Card variant="outlined">
                <CardContent sx={{ p: 2, '&:last-child': { pb: 2 } }}>
                  <Typography variant="subtitle1" sx={{ fontWeight: 600, mb: 1 }}>
                    Order Summary
                  </Typography>
                  
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
                    <Typography variant="body2">
                      Subtotal ({itemCount} items)
                    </Typography>
                    <Typography variant="body2">
                      {formatPrice(subtotal)}
                    </Typography>
                  </Box>
                  
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
                    <Typography variant="body2">Delivery Fee</Typography>
                    <Typography variant="body2">
                      {deliveryFee > 0 ? formatPrice(deliveryFee) : 'FREE'}
                    </Typography>
                  </Box>
                  
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                    <Typography variant="body2">Taxes</Typography>
                    <Typography variant="body2">
                      {formatPrice(taxes)}
                    </Typography>
                  </Box>
                  
                  <Divider sx={{ my: 1 }} />
                  
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                    <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                      Total
                    </Typography>
                    <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                      {formatPrice(total)}
                    </Typography>
                  </Box>
                  
                  <Button
                    variant="contained"
                    fullWidth
                    size="large"
                    onClick={handleCheckout}
                    sx={{ borderRadius: 2 }}
                  >
                    {isAuthenticated ? 'Proceed to Checkout' : 'Login to Checkout'}
                  </Button>
                </CardContent>
              </Card>
            </Box>
          </>
        )}
      </Box>
    </Drawer>
  );
};

export default CartSidebar;
